import { useState, useEffect } from 'react';

function Locations() {
    const [locations, setLocations] = useState([]);

    // Verileri çek
    useEffect(() => {
        fetch('/locations')
            .then(res => res.json())
            .then(data => setLocations(data))
            .catch(err => console.error(err));
    }, []);

    // Yeni veri ekle
    const addLocation = () => {
        fetch('/locations/add', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ name: 'Park', lat: 41.0082, lng: 28.9784 })
        })
            .then(res => res.json())
            .then(data => setLocations(prev => [...prev, data]))
            .catch(err => console.error(err));
    }

    return (
        <div>
            <h2>Locations List</h2>
            <button onClick={addLocation}>Yeni Lokasyon Ekle</button>
            <ul>
                {locations.map(loc => (
                    <li key={loc.id}>{loc.name} - {loc.geom}</li>
                ))}
            </ul>
        </div>
    );
}

export default Locations;
