import React from "react";
// react-bootstrap components
import { Container } from "react-bootstrap";
// Haritanızın doğru yüklenmesi için Leaflet CSS'ini index.js'te çağırdığınızdan emin olun!
// import 'leaflet/dist/leaflet.css';

function Maps() {
  const mapRef = React.useRef(null);
  const mapInstanceRef = React.useRef(null); // Harita instance'ını saklamak için ref

  React.useEffect(() => {
    const L = window.L;
    if (!L || !mapRef.current) return;

    // Haritanın Navbar ve Footer arasında kalan alanı doldurması için boyut hesaplama
    const updateMapHeight = () => {
      const mapElement = mapRef.current;
      if (!mapElement) return;

      // 1. Navbar'ı bulma
      const navbarElement = document.querySelector('.navbar');
      const navbarHeight = navbarElement ? navbarElement.offsetHeight : 0;

      // 2. Footer'ı bulma (eğer varsa)
      const footerElement = document.querySelector('footer');
      const footerHeight = footerElement ? footerElement.offsetHeight : 0;

      // Harita yüksekliğini hesapla: Pencere Yüksekliği - Navbar Yüksekliği - Footer Yüksekliği
      const availableHeight = window.innerHeight - navbarHeight - footerHeight;

      mapElement.style.height = `${availableHeight}px`;

      // Harita yeniden boyutlandığında görünümünü güncelle
      if (mapInstanceRef.current) {
        mapInstanceRef.current.invalidateSize(false);
      }
    };

    // 1. Leaflet haritasını başlat
    const centerTurkey = [39.0, 35.0];
    mapInstanceRef.current = L.map(mapRef.current, {
      zoomControl: true,
      scrollWheelZoom: true,
      attributionControl: false
    }).setView(centerTurkey, 6);

    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
      maxZoom: 19
    }).addTo(mapInstanceRef.current);

    // 2. Boyutu ilk yüklemede ayarla
    updateMapHeight();

    // 3. Ekran boyutu değiştiğinde (responsive) yeniden boyutlandır
    window.addEventListener('resize', updateMapHeight);

    // 4. Cleanup fonksiyonu
    return () => {
      window.removeEventListener('resize', updateMapHeight);
      if (mapInstanceRef.current) {
        mapInstanceRef.current.remove();
      }
    };
  }, []); // [] sayesinde sadece bileşen yüklendiğinde çalışır.

  return (
      // Haritanın, diğer component'lerinizin dışında, temiz bir kapsayıcı içinde olduğundan emin olun.
      <div className="map-container">
        <div id="map" ref={mapRef} style={{ margin: 0, padding: 0 }}></div>
      </div>
  );
}

export default Maps;
