import React from "react";

// react-bootstrap components
import { Badge, Button, Navbar, Nav, Container } from "react-bootstrap";

function Maps() {
  const mapRef = React.useRef(null);
  React.useEffect(() => {
    const L = window.L;
    if (!L || !mapRef.current) return;

    let mapInstance;

    const updateMapHeight = () => {
      const mapElement = mapRef.current;
      if (!mapElement) return;
      const footerElement = document.querySelector('footer');
      const mapTop = mapElement.getBoundingClientRect().top;
      const footerTop = footerElement
        ? footerElement.getBoundingClientRect().top
        : window.innerHeight;
      const available = Math.max(0, Math.floor(footerTop - mapTop));
      mapElement.style.height = available + 'px';
      mapElement.style.width = '100%';
      if (mapInstance) {
        mapInstance.invalidateSize(false);
      }
    };

    updateMapHeight();
    window.addEventListener('resize', updateMapHeight);

    const centerTurkey = [39.0, 35.0];
    mapInstance = L.map(mapRef.current, {
      zoomControl: true,
      scrollWheelZoom: true,
      attributionControl: false
    }).setView(centerTurkey, 6);

    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
      maxZoom: 19
    }).addTo(mapInstance);

    // No marker; only centered map
    updateMapHeight();

    return () => {
      window.removeEventListener('resize', updateMapHeight);
      if (mapInstance) {
        mapInstance.remove();
      }
    };
  }, []);
  return (
    <>
      <div className="map-container">
        <div id="map" ref={mapRef}></div>
      </div>
    </>
  );
}

export default Maps;
