import React from "react";

function Maps() {
  const mapRef = React.useRef(null);

  React.useEffect(() => {
    const L = window.L;
    if (!L || !mapRef.current) return;

    let mapInstance;

    const updateMapHeight = () => {
      const mapElement = mapRef.current;
      if (!mapElement) return;

      // Haritayı viewport'a sığdır
      const mapTop = mapElement.getBoundingClientRect().top;
      const footer = document.querySelector('footer');
      const footerTop = footer ? footer.getBoundingClientRect().top : window.innerHeight;
      const availableHeight = Math.max(0, footerTop - mapTop);

      mapElement.style.height = availableHeight + "px";
      mapElement.style.width = "100%";
      mapElement.style.margin = "0";
      mapElement.style.padding = "0";

      if (mapInstance) {
        mapInstance.invalidateSize(false);
      }
    };

    // scroll çıkmasını engelle
    const previousBodyOverflow = document.body.style.overflow;
    document.body.style.overflow = "hidden";

    updateMapHeight();
    window.addEventListener("resize", updateMapHeight);

    const centerTurkey = [39.0, 35.0];
    mapInstance = L.map(mapRef.current, {
      zoomControl: true,
      scrollWheelZoom: true,
      attributionControl: false,
    }).setView(centerTurkey, 6);

    L.tileLayer("https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png", {
      maxZoom: 19,
    }).addTo(mapInstance);

    // cleanup
    return () => {
      window.removeEventListener("resize", updateMapHeight);
      if (mapInstance) {
        mapInstance.remove();
      }
      document.body.style.overflow = previousBodyOverflow;
    };
  }, []);

  return (
      <div
          className="map-container"
          style={{
            position: "relative",
            width: "100%",
            height: "100vh", // viewport yüksekliği
            margin: 0,
            padding: 0,
          }}
      >
        <div
            id="map"
            ref={mapRef}
            style={{
              width: "100%",
              height: "100%",
            }}
        ></div>
      </div>
  );
}

export default Maps;
