import React from "react";
// react-bootstrap bileşenleri artık harita mantığı için gerekli değil, kaldırılabilir.
// import { Container } from "react-bootstrap";

function Maps() {
  const mapRef = React.useRef(null);
  const mapInstanceRef = React.useRef(null); // Harita instance'ını saklamak için

  React.useEffect(() => {
    // Leaflet global nesnesini kontrol et
    const L = window.L;
    if (!L || !mapRef.current) return;

    // Harita zaten başlatılmışsa tekrar başlatma
    if (mapInstanceRef.current) return;

    // 1. Leaflet haritasını başlat
    const centerTurkey = [39.0, 35.0];
    const mapInstance = L.map(mapRef.current, {
      zoomControl: true,
      scrollWheelZoom: true,
      attributionControl: false
    }).setView(centerTurkey, 6);

    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
      maxZoom: 19
    }).addTo(mapInstance);

    // Harita instance'ını sakla
    mapInstanceRef.current = mapInstance;

    // 2. Pencere boyutu değiştiğinde harita boyutunu güncelle
    const handleResize = () => {
      // Haritanın kapsayıcı elementini tekrar kontrol etmesini sağlar
      mapInstance.invalidateSize(false);
    };

    window.addEventListener('resize', handleResize);

    // 3. Cleanup fonksiyonu:
    return () => {
      window.removeEventListener('resize', handleResize);
      if (mapInstanceRef.current) {
        mapInstanceRef.current.remove(); // Haritayı DOM'dan temizle
        mapInstanceRef.current = null;
      }
    };
  }, []); // Sadece bileşen yüklendiğinde çalışır.

  return (
      // Haritanın CSS'te ayarladığımız 100vh yüksekliğini almasını sağlar
      <div className="map-container">
        {/* Harita kapsayıcısı, CSS'te #map olarak tanımlandı */}
        <div id="map" ref={mapRef}></div>
      </div>
  );
}

export default Maps;
