import React from "react";

// react-bootstrap components
import { Badge, Button, Navbar, Nav, Container } from "react-bootstrap";

function Maps() {
  const mapRef = React.useRef(null);
  React.useEffect(() => {
    const L = window.L;
    if (!L || !mapRef.current) return;

    const centerTurkey = [39.0, 35.0];
    const mapInstance = L.map(mapRef.current, { zoomControl: true, scrollWheelZoom: false }).setView(centerTurkey, 6);

    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
      maxZoom: 19,
      attribution: '&copy; OpenStreetMap contributors'
    }).addTo(mapInstance);

    L.marker(centerTurkey).addTo(mapInstance).bindPopup('Türkiye merkezi görünüm').openPopup();
  }, []);
  return (
      <>
        <div className="map-container">
          <div id="map" ref={mapRef} style={{ height: "70vh", width: "100%" }}></div>
        </div>
      </>
  );
}

export default Maps;
